			<script type="text/javascript">
				//******************************** Make New line in text field
				document.getElementById("message").oninput = onNewLine;
				var chat_field_height = 75;
				var msg_field_height = 3;

				function onNewLine() {
					var msg_field = document.getElementById("message").value;
					var msg_lines = msg_field.split("\n");
					var no_rows = msg_field_height / 3;
					if (no_rows <= 5) {
						if (no_rows <= 4 && msg_lines.length > no_rows) {
							msg_field_height += 3;
							chat_field_height -= 3;
						} else if (msg_lines.length < no_rows) {
							msg_field_height -= 3;
							chat_field_height += 3;
						}
						document.getElementById("message").style.height = msg_field_height.toString() + "vh";
						document.getElementsByClassName("chat-field")[0].style.height = chat_field_height.toString() + "vh";
					}
				}
			</script>

