	<script type="text/javascript">
		//Web socket connection intiation(starting a connection for the user)
		document.getElementById("send").addEventListener('click', onsubmit);
		document.getElementById("send").disabled = false;
		var conn = new WebSocket('ws://localhost:8080');


		//On opening new Connection in web socket
		conn.onopen = function(e) {
			console.log("Connection established!");

			//get stored data to get the id
			var data = localStorage.getItem("data");
			data = JSON.parse(data);

			//making a dictionary to send id to connection
			var msg = {
				"id_setter": data["id"]
			};
			conn.send(JSON.stringify(msg));
		};

		//On recieving message from other connection
		conn.onmessage = function(e) {
			data = JSON.parse(e.data);
			putOtherMessage(data);
		};


		// on pressing Send button
		function onsubmit(e) {
			e.preventDefault();

			var msg = document.getElementById('message').value;

			var date = new Date();

			var local_time = date.getHours() + ":" + date.getMinutes();
			var day = date.getDate();
			var month = date.getMonth() + 1;
			var year = date.getFullYear();

			var data = localStorage.getItem("data");
			data = JSON.parse(data);
			var myid = data["id"];
			var myusername = data["username"];
			var chat_number = localStorage.getItem("chat_number");
			var otherid = data["chats"][chat_number];
			var otherusername = data["users"][chat_number];


			var message = {
				"messages": [{
					"message": msg,
					"time": local_time,
					"day": day,
					"month": month,
					"year": year,
					"user_from": {
						"id": myid,
						"username": myusername
					},
					"user_to": {
						"id": otherid,
						"username": otherusername
					}
				}]
			};

			putMyMessage(message, day, month, year);

			conn.send(JSON.stringify(message));
			document.getElementById('message').value = "";
		}


		//function to put message in my view
		function putMyMessage(message, day, month, year) {
			var chat_field = document.getElementsByClassName("chat-field")[0];
			var last_msg = JSON.parse(sessionStorage.getItem("last_msg"));

			if (last_msg == null) {
				message_separator = "<div class=\"date-seprator\"><h4>Today</h4></div>" + "\n";
				chat_field.insertAdjacentHTML("afterbegin", message_separator);
			} else {
				var last_msg_day = last_msg["day"];
				var last_msg_month = last_msg["month"];
				var last_msg_year = last_msg["year"];

				msg_day_of_year = dayOfTheYear(day, month, year);
				last_msg_day_of_year = dayOfTheYear(last_msg_day, last_msg_month, last_msg_year);

				if (msg_day_of_year[1] != last_msg_day_of_year[1]) {
					message_separator = "<div class=\"date-seprator\"><h4>Today</h4></div>" + "\n";
					chat_field.insertAdjacentHTML("afterbegin", message_separator);
				}
			}

			var message = "<div class=\"you\"><div class=\"message-content\"><div class=\"message-text\">" + message["messages"][0]["message"] + "</div><div class=\"message-time\">" + message["messages"][0]["time"] + "</div></div></div>";
			chat_field.insertAdjacentHTML('afterbegin', message);
		}

		//function to get index in an array by value
		function getIndex(value, array) {
			for (var i = 0; i < array.length; i++) {
				if (array[i] == value) {
					return i;
				}
			}
		}

		//function to put message in other view
		function putOtherMessage(message) {
			var chat_field = document.getElementsByClassName("chat-field")[0];

			var day = message["messages"][0]["day"];
			var month = message["messages"][0]["month"];
			var year = message["messages"][0]["year"];

			var last_msg = sessionStorage.getItem("last_msg");
			var last_msg_day = last_msg["day"];
			var last_msg_month = last_msg["month"];
			var last_msg_year = last_msg["year"];

			msg_day_of_year = dayOfTheYear(day, month, year);
			last_msg_day_of_year = dayOfTheYear(last_msg_day, last_msg_month, last_msg_year);

			let data = JSON.parse(localStorage.getItem("data"));

			let users_id = data["chats"];
			let index = getIndex(message["messages"][0]["user_to"]["id"], data["chats"]);
			let photo, link;

			if (data["type"][index] == "chef") {
				photo = data["photo"][index];
				link = "chef.php?id=" + message["messages"][0]["user_to"]["id"];
			} else {
				photo = "img/1.jpg";
				link = "#";
			}

			var message = "<div class=\"other message\"><div class=\"message-content\"><a href=\"" + link + "\"><img src=\"" + photo + "\" alt=\"profile-image\"></a><div class=\"message-text\">" + message["messages"][0]["message"] + "</div><div class=\"message-time\">" + message["messages"][0]["time"] + "</div></div></div>";
			chat_field.insertAdjacentHTML('afterbegin', message);
		}
	</script>

